document.addEventListener("DOMContentLoaded",()=>{CodeBlock.init()});const CodeBlock={maxHeight:450,initCodeBlockOverflowY:function(e){e=e||CodeBlock.maxHeight;const t=document.querySelectorAll(".highlight");t.forEach(t=>{const n=t.querySelector(".chroma"),s=n.offsetHeight;if(s>e){n.style.maxHeight=e+"px",n.style.overflow="hidden";const o=document.createElement("div");o.style.cssText="text-align: center;margin-top: -18px;cursor: pointer;",o.innerHTML='<i class="fa-solid fa-angles-down fa-beat-fade" style="font-size: 12px;"></i>',o.querySelector("i").style.color="#a19e98",t.appendChild(o),o.addEventListener("click",function(){o.innerHTML.indexOf("fa-angles-down")>0?(n.style.maxHeight=s+"px",o.innerHTML='<i class="fa-solid fa-angles-up fa-beat-fade" style="font-size: 12px;"></i>'):(n.style.maxHeight=e+"px",o.innerHTML='<i class="fa-solid fa-angles-down fa-beat-fade" style="font-size: 12px;"></i>',o.querySelector("i").style.color="#a19e98")})}})},init:function(){this.initCodeBlockOverflowY()}}